package com.example.dodo.sensores;import android.content.Context;import android.hardware.Sensor;import android.hardware.SensorEvent;import android.hardware.SensorEventListener;import android.hardware.SensorManager;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.widget.TextView;public class DetalheSensorActivity extends AppCompatActivity implements SensorEventListener {    SensorManager sensorManager;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_detalhe_sensor);        sensorManager = (SensorManager) getSystemService(Context.SENSOR_SERVICE);        int type = getIntent().getIntExtra("tipo",0);        sensorManager.registerListener(                this,                sensorManager.getDefaultSensor(type),                SensorManager.SENSOR_DELAY_NORMAL        );    }    @Override    public void onSensorChanged(SensorEvent sensorEvent) {        float[] valores = sensorEvent.values;        TextView txt1 = (TextView) findViewById(R.id.text1);        TextView txt2 = (TextView) findViewById(R.id.text2);        TextView txt3 = (TextView) findViewById(R.id.text3);        txt1.setText(""+ valores[0]);        txt2.setText(""+ valores[1]);        txt3.setText(""+ valores[2]);    }    @Override    public void onAccuracyChanged(Sensor sensor, int i) {    }    @Override    protected void onDestroy() {        super.onDestroy();        sensorManager.unregisterListener(this);    }}