package com.example.dodo.sensores;import android.app.ListActivity;import android.content.Context;import android.content.Intent;import android.hardware.Sensor;import android.hardware.SensorManager;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.view.View;import android.widget.ArrayAdapter;import android.widget.ListView;import java.util.ArrayList;import java.util.List;public class MainActivity extends ListActivity {    SensorManager sensorManager;    List<Sensor> sensorList;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        sensorManager = (SensorManager) getSystemService(Context.SENSOR_SERVICE);        sensorList =  sensorManager.getSensorList(Sensor.TYPE_ALL);        List<String> nomeSensores = new ArrayList<>();        for(Sensor sensor: sensorList){            nomeSensores.add(sensor.getName());        }        setListAdapter(                new ArrayAdapter<String>(this, android.R.layout.simple_list_item_1, nomeSensores)        );    }    @Override    protected void onListItemClick(ListView l, View v, int position, long id) {        super.onListItemClick(l, v, position, id);        Intent it = new Intent(this, DetalheSensorActivity.class);        it.putExtra("tipo", sensorList.get(position).getType());        startActivity(it);    }}